import &#x7B; strict as assert &#x7D; from "assert";
import &#x7B; traverse &#x7D; from "ast-monkey-traverse-with-lookahead";

const input = [
  &#x7B;
    a: "b",
  &#x7D;,
  &#x7B;
    c: "d",
  &#x7D;,
  &#x7B;
    e: "f",
  &#x7D;,
];
const gathered = [];

// callback interface:
traverse(
  input,
  (key1, val1, innerObj) => &#x7B;
    gathered.push([key1, val1, innerObj]);
  &#x7D;,
  1 // <---------------- report one upcoming value
);

assert.deepEqual(gathered, [
  // ===================
  [
    &#x7B;
      a: "b",
    &#x7D;,
    undefined,
