import &#x7B; strict as assert &#x7D; from "assert";
import combinations from "object-boolean-combinations";

assert.deepEqual(
  combinations(&#x7B;
    a: true, // values don't matter as long as they're boolean
    b: false,
    c: true,
  &#x7D;),
  [
    &#x7B; a: 0, b: 0, c: 0 &#x7D;,
    &#x7B; a: 1, b: 0, c: 0 &#x7D;,
    &#x7B; a: 0, b: 1, c: 0 &#x7D;,
    &#x7B; a: 1, b: 1, c: 0 &#x7D;,
    &#x7B; a: 0, b: 0, c: 1 &#x7D;,
    &#x7B; a: 1, b: 0, c: 1 &#x7D;,
    &#x7B; a: 0, b: 1, c: 1 &#x7D;,
    &#x7B; a: 1, b: 1, c: 1 &#x7D;,
  ]
);
// you get 2^n plain objects in an array
