import &#x7B; strict as assert &#x7D; from "assert";
import &#x7B; flattenReferencing &#x7D; from "object-flatten-referencing";

assert.deepEqual(
  flattenReferencing(
    &#x7B;
      key1: "val11.val12",
      key2: "val21.val22",
    &#x7D;,
    &#x7B;
      key1: "Contact us",
      key2: "Tel. 0123456789",
    &#x7D;
  ),
  &#x7B;
    key1: "%%_val11.val12_%%",
    key2: "%%_val21.val22_%%",
  &#x7D;
);
