import &#x7B; strict as assert &#x7D; from "assert";
import setAllValuesTo from "object-set-all-values-to";

assert.deepEqual(
  setAllValuesTo(&#x7B;
    a: "a",
    b: "b",
    c: "c",
    d: "d",
  &#x7D;),
  &#x7B;
    a: false,
    b: false,
    c: false,
    d: false,
  &#x7D;
);

// you can change the default "false" to something else:
assert.deepEqual(
  setAllValuesTo(
    &#x7B;
      a: "a",
      b: "b",
      c: "c",
      d: "d",
    &#x7D;,
    "x"
  ),
  &#x7B;
    a: "x",
    b: "x",
    c: "x",
    d: "x",
  &#x7D;
);
