import &#x7B; strict as assert &#x7D; from "assert";
import &#x7B; findMalformed &#x7D; from "string-find-malformed";

// Below, we look for dodgy cases of `<!--`
const gathered = [];
findMalformed(
  "<div><!-something--></div>",
  "<!--",
  // your callback function:
  (obj) => &#x7B;
    gathered.push(obj);
  &#x7D;,
  &#x7B;
    maxDistance: 1, // Levenshtein distance
  &#x7D;
);
assert.deepEqual(gathered, [
  &#x7B;
    idxFrom: 5,
    idxTo: 8,
  &#x7D;,
]);
