import &#x7B; strict as assert &#x7D; from "assert";
import &#x7B; stri &#x7D; from "stristri";

// strips both HTML and Nunjucks, leaves text only:
assert.equal(
  stri(
    `<html><div>The price is&#x7B;% if data.price > 100 %&#x7D; high&#x7B;% endif %&#x7D;</div>`,
    &#x7B;
      html: true,
      css: true,
      text: false,
      templatingTags: true,
    &#x7D;
  ).result,
  "The price is high"
);
